import { ISubmittableResult } from '@polkadot/types/types';
import { Balance } from '@polkadot/types/interfaces';
import Account from './account';
import Contract from './contract';
import NetworkProvider from './provider';
export default class Interactor {
    provider: NetworkProvider;
    account: Account;
    constructor(provider: NetworkProvider, account: Account);
    transfer(recipientAddress: string, value: number, callback: ((result: ISubmittableResult) => void)): Promise<() => void>;
    getBalance(): Promise<Balance>;
    getAddress(): string;
    newContract(dotContract: {
        source: {
            wasm: string;
        };
    }): Contract;
}
